my_set = {1, 2, 3}
my_set.add(4)       # Thêm phần tử 4 vào tập hợp
my_set.remove(2)    # Xóa phần tử 2 khỏi tập hợp
print(my_set)       # Kết quả: {1, 3, 4}

set1 = {1, 2, 3}
set2 = {3, 4, 5}
union_set = set1.union(set2)                  # Hợp của hai tập hợp
intersection_set = set1.intersection(set2)    # Giao của hai tập hợp
difference_set = set1.difference(set2)        # Hiệu của hai tập hợp
symmetric_difference_set = set1.symmetric_difference(set2)  # Phần tử không chung của hai tập hợp
print(union_set)                  # Kết quả: {1, 2, 3, 4, 5}
print(intersection_set)           # Kết quả: {3}
print(difference_set)             # Kết quả: {1, 2}
print(symmetric_difference_set)   # Kết quả: {1, 2, 4, 5}
